mapper.select:
select * from CART_HISTORY_INFO


		AND UI_NUM = #{uiNum}


		AND PI_NUM = #{piNum}

SELECT COUNT(1) FROM CART_HISTORY_INFO
WHERE UI_NUM = #{uiNum}
AND PI_NUM = #{piNum}

mapper.update:
UPDATE CART_HISTORY_INFO
SET CHI_QTY=#{chiQty}
WHERE
CHI_NUM = #{chiNum}

UPDATE CART_HISTORY_INFO
SET CHI_QTY= CHI_QTY +
#{chiQty}
WHERE UI_NUM = #{uiNum}
AND PI_NUM = #{piNum}
